import "./Create.css";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Create () {
    const [form, setForm] = useState({
        username: "",
        password: "",
       // confirmPassword: "",
        type: ""
    });
    const [invalidMessage, setInvalidMessage] = useState("");
	const navigate = useNavigate();

	function updateForm(value) {
		return setForm((prev) => {
		  return { ...prev, ...value };
		});
	  }

    async function handleSubmit(e) {
        e.preventDefault();
       // let passwordCheck = 1;
        console.log("In onSubmit");
        console.log(form);

        // check to make sure passwords match
        // if (form.password.toString() === form.confirmPassword.toString()) {
        //     passwordCheck = 0;
        // } else {
        //     console.log("Password error");
        //     setInvalidMessage("Passwords must match");
        //     passwordCheck = 1;
        // }

        // if(passwordCheck === 0) {
			const newPerson = { ...form };
			delete newPerson.message
	  
			const response = await fetch("http://localhost:4000/accounts/add", {
			  method: "POST",
			  headers: {
				"Content-Type": "application/json",
			  },
			  body: JSON.stringify(newPerson),
			})
			  .catch(error => {
				window.alert(error);
				return;
			  });
	  
			const user = await response.json();
	  
			console.log("From create: " + user.insertedId);
	  	  
			if (user.message == null) {
			  navigate(-1);
			}

        setForm({username: "", password: "", type: ""});
        //}
    }

    return (
    <div className="container">
        <h3>New Account</h3>
        <form onSubmit={handleSubmit}>
            <label htmlFor="username" className="form-label">Username</label>
            <input type="text" placeholder="Please enter username" 
            onChange={(e) => updateForm({username: e.target.value})} required/>

            <label htmlFor="password" className="form-label">Password</label>
            <input type="text" placeholder="Please enter password" 
            onChange={(e) => updateForm({password: e.target.value})} required/>
        
            {/* <label htmlFor="confirmPassword" className="form-label">Confirm Password</label>
            <input type="text" placeholder="Please confirm password" 
            onChange={(e) => updateForm(e)} required/> */}

            <label htmlFor="type" className="form-label">Account Type</label>
            <select name="type" id="type" onChange={(e) => updateForm({type: e.target.value})}>
                <option value="business">Business</option>
                <option value="general">General</option>
                <option value="student">Student</option>
                <option value="travel">Travel</option>
            </select>
            <br />
			<input type="submit" value="Create Account" />
        </form>
        {invalidMessage}
    </div>
    );
}
