    //         setWord(word);
    //     }

    //     getVocabWord();
    //     console.log(word);
    // }


    return (
        <div className="container">
            <p>testList</p>
            <button onClick={() => onClickHandler(testList)}>Get Word</button>
            <h1>Vocabulary Practice</h1>
            <div className="word">{testList[0]}</div>
            <div className="word">{testList[1]}</div>
        </div >
    )
}
